import { redirect } from "react-router-dom";

export function getAuthToken() {
    const token = localStorage.getItem('token');
    return token;
}

export function tockenLoader(){
    return getAuthToken();
}

export function checkAuthLoader() {
    const token = getAuthToken();
    if(!token){
        return redirect('/');
    }
    return null
}
