}

export default EventDetailPage;

export const loader = async (req, params) => {
    const id = params.id;
    const response = await fetch(`http://localhost:8080/events/${id}`)
    if (!response.ok) {
        throw json({message: 'Could not fetch details for selected event!'}, {status: 500})
    }else{
        return response;
    }
}
